installation-check.script:

function InstallationCheck() {
  if(!system.files.fileExistsAtPath('@NEXT_ROOT@')) {
    my.result.title = system.localizedString('NoSDKTitle');
    my.result.message = system.localizedString('NoSDKMessage');
    my.result.type = 'Fatal';
    return false;
  }
  if(!system.files.fileExistsAtPath('@SDK_NEXT_ROOT@/usr/local/lib/libusb.dylib')) {
    my.result.title = system.localizedString('NoLibusbSDKTitle');
    my.result.message = system.localizedString('NoLibusbSDKMessage');
    my.result.type = 'Fatal';
    return false;
  }
  return true;
}
